mapper.select:
 		select * from sys_log z 



				username like CONCAT(CONCAT('%',#{key}),'%') or operation like CONCAT(CONCAT('%',#{key}),'%')



			limit #{offset}, #{limit}

mapper.update:
update sys_log
username = #{username}, operation = #{operation}, method = #{method}, params = #{params}, ip = #{ip}
where id = #{id}
